import React from "react";
import { useParams } from "react-router-dom";
import Popup from "reactjs-popup";
import NameInput from "./partials/NameInput";
import styles from "./index.module.scss";

function PollScene() {
  let { pollId } = useParams();
  return (
    <div className={styles.pollScene}>
      <svg
        height="100%"
        width="100%"
        id="svg"
        viewBox="0 0 1440 600"
        xmlns="http://www.w3.org/2000/svg"
        class="transition duration-300 ease-in-out delay-150"
      >
        <defs>
          <linearGradient id="gradient">
            <stop offset="5%" stop-color="#002bdc88"></stop>
            <stop offset="95%" stop-color="#32ded488"></stop>
          </linearGradient>
        </defs>
        <path
          d="M 0,600 C 0,600 0,200 0,200 C 108.32142857142858,231.57142857142858 216.64285714285717,263.14285714285717 337,265 C 457.35714285714283,266.85714285714283 589.7499999999999,239.00000000000003 725,212 C 860.2500000000001,184.99999999999997 998.3571428571429,158.85714285714283 1118,157 C 1237.642857142857,155.14285714285717 1338.8214285714284,177.57142857142858 1440,200 C 1440,200 1440,600 1440,600 Z"
          stroke="none"
          stroke-width="0"
          fill="url(#gradient)"
          class="transition-all duration-300 ease-in-out delay-150"
          transform="rotate(-180 720 300)"
        ></path>
        <defs>
          <linearGradient id="gradient">
            <stop offset="5%" stop-color="#002bdcff"></stop>
            <stop offset="95%" stop-color="#32ded4ff"></stop>
          </linearGradient>
        </defs>
        <path
          d="M 0,600 C 0,600 0,400 0,400 C 89.71428571428572,434.8571428571429 179.42857142857144,469.7142857142857 312,445 C 444.57142857142856,420.2857142857143 620,336 759,325 C 898,314 1000.5714285714287,376.2857142857143 1108,401 C 1215.4285714285713,425.7142857142857 1327.7142857142858,412.8571428571429 1440,400 C 1440,400 1440,600 1440,600 Z"
          stroke="none"
          stroke-width="0"
          fill="url(#gradient)"
          class="transition-all duration-300 ease-in-out delay-150"
          transform="rotate(-180 720 300)"
        ></path>
      </svg>
      {/* <h3>Requested topic ID: {pollId}</h3> */}
      <Popup
        // trigger={<button className="button"> Open Modal </button>}
        modal
        defaultOpen={!window.localStorage.getItem("loggedUser")}
        closeOnDocumentClick={false}
        className="my-popup-content"
        contentStyle={{
          width: "400px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        {(close) => <NameInput close={close} />}
      </Popup>
      <div
        className={styles.pollResults}
        style={{
          width: "400px",
        }}
      >
        test
      </div>
    </div>
  );
}

export default PollScene;
