import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { useParams } from "react-router";
import { enterUser } from "../../../../utils/database.utils";
import firebase from "firebase";

function NameInput({ close }) {
  const [name, setName] = useState("");
  let { pollId } = useParams();

  const pushUser = () => {
    const userRef = firebase.database().ref(pollId);
    userRef
      .get()
      .then((snapshot) => {
        if (snapshot.exists()) {
          console.log(snapshot.val());
          const users = snapshot.val();
          for (let id in users) {
            if (users[id].name === name) {
              alert("User with same name already in room!!");
              setName("");
            } else {
              window.localStorage.setItem("loggedUser", name);
              enterUser(name, pollId);
              close();
            }
          }
        } else {
          console.log("No data available");
          window.localStorage.setItem("loggedUser", name);
          enterUser(name, pollId);
          close();
        }
      })
      .catch((error) => {
        console.error(error);
      });
  };

  const handleInput = (e) => {
    setName(e.target.value);
  };

  return (
    <form noValidate autoComplete="off" onSubmit={pushUser}>
      <div
        style={{
          textAlign: "center",
          padding: "20px",
        }}
      >
        <h3>Please enter your name</h3>
        <TextField
          id="outlined-basic"
          variant="outlined"
          onChange={(e) => handleInput(e)}
          fullWidth
          required
          value={name}
        />
        <Button
          variant="contained"
          color="primary"
          disabled={!name.length}
          onClick={() => {
            if (name.length) {
              pushUser();
            }
          }}
          style={{
            marginTop: "30px",
          }}
        >
          Start Game
        </Button>
      </div>
    </form>
  );
}

export default NameInput;
