import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Popup from "reactjs-popup";
import NameInput from "./partials/NameInput";
import firebase from "firebase";
import styles from "./index.module.scss";
import PollResults from "./partials/PollResults";

function PollScene() {
  let { pollId } = useParams();
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const userRef = firebase.database().ref(pollId);
    userRef.on("value", (snapshot) => {
      const users = snapshot.val();
      const loggedUsers = [];
      for (let id in users) {
        loggedUsers.push(users[id]);
      }
      console.log("loggedUsers", loggedUsers);
      setUsers(loggedUsers);
    });
  });

  return (
    <div className={styles.pollScene}>
      {/* <h3>Requested topic ID: {pollId}</h3> */}
      <div className={styles.brandName}>SprintPlan</div>
      <Popup
        modal
        defaultOpen={!window.localStorage.getItem("loggedUser")}
        closeOnDocumentClick={false}
        className="my-popup-content"
        contentStyle={{
          width: "400px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        {(close) => <NameInput close={close} />}
      </Popup>
      <div className={styles.pollResults}>
        <PollResults />
      </div>
      <div>
        {users.map((user) => {
          return <div>{user.name}</div>;
        })}
      </div>
    </div>
  );
}

export default PollScene;
