import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Popup from "reactjs-popup";
import NameInput from "./partials/NameInput";
import firebase from "firebase";
import styles from "./index.module.scss";
import PollResults from "./partials/PollResults";
import UsersList from "./partials/UsersList";
import { enterUser, getCurrentUser } from "../../utils/database.utils";

function PollScene() {
  let { pollId } = useParams();
  const [loggedUsers, setLoggedUsers] = useState([]);

  useEffect(() => {
    getLoggedUsers();
  }, []);

  const getLoggedUsers = () => {
    const userRef = firebase.database().ref(pollId);
    const currentUser = getCurrentUser();
    userRef.on("value", (snapshot) => {
      const users = snapshot.val();
      const tempUsers = [];
      for (let id in users) {
        if (users[id].name === getCurrentUser()) {
          window.localStorage.setItem({
            userObject: JSON.stringify({ id, ...users[id] }),
          });
        }
        tempUsers.push({ id, ...users[id] });
      }
      const firebaseLogged = tempUsers.filter(
        (entry) => entry.name === currentUser
      );
      if (!firebaseLogged.length) {
        enterUser(getCurrentUser(), pollId);
      }
      console.log("firebaseLogged", firebaseLogged);
      const userWithID = loggedUsers.filter(
        (entry) => entry.name === currentUser
      );
      const currentUserId = userWithID[0].id;
      const userRef = firebase.database().ref(pollId).child(currentUserId);
      userRef.onDisconnect().remove();
      setLoggedUsers(tempUsers);
    });
  };

  return (
    <div className={styles.pollScene}>
      {/* <h3>Requested topic ID: {pollId}</h3> */}
      <div className={styles.brandName}>SprintPlan</div>
      <Popup
        modals
        defaultOpen={!window.localStorage.getItem("loggedUser")}
        closeOnDocumentClick={false}
        className="my-popup-content"
        contentStyle={{
          width: "400px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        {(close) => <NameInput close={close} />}
      </Popup>
      <div className={styles.pollResults}>
        <PollResults loggedUsers={loggedUsers} />
      </div>
      <div className={styles.usersWrapper}>
        {loggedUsers && loggedUsers.length ? (
          <UsersList users={loggedUsers} />
        ) : (
          <h3>Waiting for others to join</h3>
        )}
      </div>
    </div>
  );
}

export default PollScene;
