import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Popup from "reactjs-popup";
import NameInput from "./partials/NameInput";
import firebase from "firebase";
import styles from "./index.module.scss";
import PollResults from "./partials/PollResults";
import UsersList from "./partials/UsersList";
import { enterUser } from "../../utils/database.utils";

function PollScene({ loggedUser }) {
  let { pollId } = useParams();
  const [loggedUsers, setLoggedUsers] = useState([]);
  const [openModal, setOpenModal] = useState(
    !window.localStorage.getItem("loggedUser")
  );
  const [loggedId, setLoggedId] = useState("");

  useEffect(() => {
    getLoggedUsers();
  }, []);

  //   const checkCurrentUserLogged = () => {
  //     if (!window.localStorage.getItem("loggedUser")) {
  //       setOpenModal(true);
  //     } else {
  //       const filtered = loggedUsers.filter(
  //         (user) => user.name === window.localStorage.getItem("loggedUser")
  //       );
  //       console.log("loggedUsers", filtered);
  //       if (!filtered.length) {
  //         enterUser(window.localStorage.getItem("loggedUser"), pollId);
  //         // getLoggedUsers();
  //       }
  //     }
  //     // var ref = firebase.database().ref(`${pollId}/${loggedId}`);
  //     // ref.onDisconnect().remove();
  //   };

  const getLoggedUsers = () => {
    const userRef = firebase.database().ref(pollId);
    userRef.on("value", (snapshot) => {
      const users = snapshot.val();
      const tempUsers = [];
      for (let id in users) {
        if (users[id].name === window.localStorage.getItem("loggedUser")) {
          setLoggedId(id);
        }
        tempUsers.push({ id, ...users[id] });
      }
      setLoggedUsers(tempUsers);
      console.log("tempUsers", tempUsers);
    });
    const currentRef = firebase.database().ref(pollId).child(loggedId);
    currentRef.onDisconnect().remove();
  };

  return (
    <div className={styles.pollScene}>
      <div className={styles.brandName}>SprintPlan</div>
      <Popup
        open={openModal}
        modals
        defaultOpen={!window.localStorage.getItem("loggedUser")}
        closeOnDocumentClick={false}
        className="my-popup-content"
        contentStyle={{
          width: "400px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        {(close) => (
          <NameInput close={close} openModal={() => setOpenModal(true)} />
        )}
      </Popup>
      <div className={styles.pollResults}>
        <PollResults loggedUsers={loggedUsers} loggedUser={loggedUser} />
      </div>
      <div className={styles.usersWrapper}>
        {loggedUsers && loggedUsers.length ? (
          <UsersList users={loggedUsers} />
        ) : (
          <h3>Waiting for others to join</h3>
        )}
      </div>
    </div>
  );
}

export default PollScene;
