import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { useParams } from "react-router";
import { enterUser } from "../../../../utils/database.utils";
import firebase from "firebase";

function NameInput({ close }) {
  const [name, setName] = useState("");
  let { pollId } = useParams();

  const addUser = () => {
    const dbRef = firebase.database().ref();
    dbRef
      .child("polls")
      .child(pollId)
      .get()
      .then((snapshot) => {
        if (snapshot.exists()) {
          console.log(snapshot.val());
        } else {
          console.log("No data available");
        }
      })
      .catch((error) => {
        console.error(error);
      });
  };

  const pushUser = () => {
    const userRef = firebase.database().ref(pollId);
    userRef.on("value", (snapshot) => {
      const users = snapshot.val();
      const tempUsers = [];
      for (let id in users) {
        if (users[id].name === name) {
          setName(`${name}1`);
        }
        tempUsers.push({ id, ...users[id] });
        // setLoggedUsers(tempUsers);
        // if (users[id].name === window.localStorage.getItem("loggedUser")) {
        //   window.localStorage.setItem("loggedId", id);
        //   loggedId = id;
        // }
        console.log("tempUsersNI", tempUsers);
      }
    });
    // enterUser(name, pollId);
    addUser();
  };

  return (
    <div
      style={{
        textAlign: "center",
        padding: "20px",
      }}
    >
      <h3>Please enter your name</h3>
      <TextField
        id="outlined-basic"
        variant="outlined"
        onChange={(e) => setName(e.target.value)}
        fullWidth
        required
      />
      <Button
        variant="contained"
        color="primary"
        disabled={!name.length}
        onClick={() => {
          if (name.length) {
            window.localStorage.setItem("loggedUser", name);
            pushUser();
            close();
          }
        }}
        style={{
          marginTop: "30px",
        }}
      >
        Start Game
      </Button>
    </div>
  );
}

export default NameInput;
