import firebase from "firebase";
import React, { useEffect, useState } from "react";
import roomImg from "../../../../Assets/Images/room.png";
import userImg from "../../../../Assets/Images/user.png";
import styles from "../../index.module.scss";
import { useHistory, useParams } from "react-router";
import axios from "axios";

function PollResults({ loggedUsers, loggedUser }) {
  const [pollData, setPollData] = useState({});
  const [selectedPoll, setSelectedPoll] = useState("");
  const currentUser = window.localStorage.getItem("loggedUser");
  const { pollId } = useParams();

  function setHasVoted() {
    const userWithID = loggedUsers.filter(
      (entry) => entry.name === currentUser
    );
    const currentUserId = userWithID[0].id;
    const userRef = firebase.database().ref(pollId).child(currentUserId);
    userRef.update({
      voted: !selectedPoll.length,
    });
  }

  useEffect(() => {
    fetchCurrentPollData();
  }, []);

  let history = useHistory();

  function fetchCurrentPollData() {
    var db = firebase.firestore();
    db.collection("polls")
      .doc(pollId)
      .onSnapshot((doc) => {
        if (doc.data()) {
          console.log("doc.data()", doc.data());
          setPollData(doc.data());
        } else {
          history.push("/");
        }
      });
  }

  return (
    <div className={styles.resultsWrapper}>
      <div className={styles.cardHeader}>
        <div className={styles.pollName}>
          <img src={roomImg} alt="RoomName" className={styles.roomImg} />
          {pollData.roomName}
        </div>
        <div className={styles.logged}>
          <img src={userImg} alt="User" className={styles.userImg} />
          {loggedUser ? loggedUser.displayName : "User"}
        </div>
      </div>
      <div className={`${styles.seriesWrapper}`}>
        {pollData.series &&
          pollData.series.length &&
          pollData.series.map((entry, key) => {
            return (
              <div
                key={key}
                className={`${styles.entryCard} ${
                  selectedPoll === entry.entryValue ? styles.selectedPoll : ""
                }`}
                onClick={() => {
                  setHasVoted();
                  setSelectedPoll(
                    entry.entryValue === selectedPoll ? "" : entry.entryValue
                  );
                }}
              >
                {entry.entryValue}
              </div>
            );
          })}
        {/* <button onClick={() => console.log("pollData", pollData)}>check</button> */}
      </div>
    </div>
  );
}

export default PollResults;
