import React, { useState } from "react";
import styles from "./index.module.scss";
import TextField from "@material-ui/core/TextField";
import MenuItem from "@material-ui/core/MenuItem";
import Select from "@material-ui/core/Select";
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel";
import Button from "@material-ui/core/Button";

function StartPollForm() {
  const [roomName, setRoomName] = useState("");
  const [series, setSeries] = useState([]);
  const [allCanSee, setAllCanSee] = useState(false);

  const handleSeriesSelect = (event) => {
    setSeries(event.target.value);
  };

  const handleAccesSelect = (event) => {
    setAllCanSee(event.target.value);
  };

  const SERIES = [
    ["0", "1", "2", "3", "5", "8", "13", "21"],
    ["0", "1/2", "1", "2", "3", "5", "8", "13"],
  ];

  const createPoll = () => {
    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ roomName, series, allCanSee }),
    };
    fetch("https://localhost:4000/polls", requestOptions)
      .then((response) => response.json())
      .then((data) => window.localStorage.setItem("roomId", data.roomId));
  };

  return (
    <div className={styles.pollform}>
      Start New Game
      <div className={styles.form}>
        <div className={styles.formField}>
          <TextField
            id="outlined-basic"
            label="Enter Game's Name"
            variant="outlined"
            onChange={(e) => setRoomName(e.target.value)}
            fullWidth
            required
          />
        </div>
        <div className={styles.formField}>
          <FormControl variant="outlined" fullWidth>
            <InputLabel id="demo-customized-select-label">Series</InputLabel>
            <Select
              labelId="demo-simple-select-outlined-label"
              id="demo-simple-select-outlined"
              label="Series"
              onChange={handleSeriesSelect}
              fullWidth
              required
            >
              <MenuItem value={`${SERIES[0]}`}>
                {SERIES[0].map((entry) => `${entry}, `)}
              </MenuItem>
              <MenuItem value={`${SERIES[1]}`}>
                {SERIES[1].map((entry) => `${entry}, `)}
              </MenuItem>
            </Select>
          </FormControl>
        </div>
        <div className={styles.formField}>
          <FormControl variant="outlined" fullWidth>
            <InputLabel id="demo-customized-select-label">
              Who can show cards?
            </InputLabel>
            <Select
              labelId="demo-simple-select-outlined-label"
              id="demo-simple-select-outlined"
              label="Who can view results?"
              fullWidth
              onChange={handleAccesSelect}
              required
            >
              <MenuItem value={false}>Only Me</MenuItem>
              <MenuItem value={true}>Everyone</MenuItem>
            </Select>
          </FormControl>

          <div className={styles.playBtn}>
            <Button
              variant="contained"
              color="primary"
              href="#contained-buttons"
              onClick={() => createPoll()}
            >
              Start Game
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default StartPollForm;
