import firebase from "firebase";

export const enterUser = (name, pollId) => {
  const userRef = firebase.database().ref(pollId);
  const user = {
    name,
    voted: false,
  };
  userRef.push(user);
};

export const getCurrentUser = window.localStorage.getItem("loggedUser");
